mapper.insert:
   insert into download_records (did, ip, ua, created_at
  )
values (#{did,jdbcType=VARCHAR}, #{ip,jdbcType=VARCHAR}, #{ua,jdbcType=VARCHAR}, #{createdAt,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT date(created_at) as `name` , count(1) as count FROM `download_records`
where
 created_at >= #{beginTime,jdbcType=DATE}

    and  created_at <= #{endTime,jdbcType=DATE} 

GROUP BY date(created_at)

SELECT DATE_FORMAT(created_at,'%Y-%m') as `name` , count(1) as count FROM `download_records`
where
 created_at >= #{beginTime,jdbcType=DATE}

    and  created_at <= #{endTime,jdbcType=DATE} 

GROUP BY DATE_FORMAT(created_at,'%Y-%m')
